import Button from '@mui/material/Button'
import { useEffect } from 'react'
import { FormProvider, useForm } from 'react-hook-form'
import { useAppSelector } from '../../store/hooks'
import { selectSelectedProduct } from '../../store/Products'
import { Product } from '../../types/Product'
import { Flexbox } from '../Flexbox'
import { FormTextField } from '../FormTextFIeld'

const initialProductForm: Product = {
    id: undefined,
    name: '',
    description: '',
    price: 0
}
export const ProductForm = () => {
  const selectedProduct = useAppSelector(selectSelectedProduct)
  const methods = useForm({ defaultValues: initialProductForm})

  useEffect(() => {
    methods.reset(selectedProduct)
  }, [selectedProduct, methods])
  return (
    <FormProvider {...methods}>
        <form onSubmit={methods.handleSubmit((data) => {
            console.log(data)
        })}>
      <h1>Product</h1>
      <Flexbox flexDirection="column" gap="20px">
        <FormTextField name="name"  defaultValue={selectedProduct?.name}/>
      </Flexbox>

      <Button variant="contained" type="submit">
        Save
      </Button>
      </form>
    </FormProvider>
  )
}
