import { createSlice, createSelector, PayloadAction } from '@reduxjs/toolkit'
import { RootState } from '..'
import { Product } from '../../types/Product'

interface ProductsState {
  selectedProductId: string
  data: Product[],
}

const initialState = {
  data: [
    { id: '1', name: 'Coffee', description: 'Tasty Coffee', price: 10 },
    { id: '2', name: 'Chocolate', description: 'Tasty Chocolate', price: 30 },
    { id: '3', name: 'Sugar', description: 'Tasty Sugar', price: 15 },
  ],
} as ProductsState

const productsSlice = createSlice({
  name: 'products',
  initialState,
  reducers: {
    deleteProduct: (state, { payload }: PayloadAction<string>) => {
      state.data = state.data.filter((product) => product.id !== payload)
    },
    setSelectedProductId: (state, { payload }: PayloadAction<string>) => {
      state.selectedProductId = payload
    },
  },
})

export const { deleteProduct, setSelectedProductId } = productsSlice.actions
export const productsReducer = productsSlice.reducer

export const selectProducts = (state: RootState) => state.products.data
export const selectSelectedProductId = (state: RootState) => state.products.selectedProductId

export const selectSelectedProduct = createSelector(
  selectProducts,
  selectSelectedProductId,
  (products, selectedProductId) => products.find(product => product.id === selectedProductId)
)
